import React, { Component } from 'react';
import ReactDOM from 'react-dom';
//import Modal from "./components/Modal";
import NavBar from "./components/NavBar"
import Main from "./pages/Main";
import ReactModal from 'react-modal';
const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)'
    }
};

ReactModal.setAppElement('#root')


class App extends Component {

    constructor() {
        super();
        this.openModal = this.openModal.bind(this);
        this.afterOpenModal = this.afterOpenModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    state = {
        score: 0,
        highScore: 0,
        wins: 0,
        loses: 0,
        modalIsOpen: false,
        title: "",
        subtitle: "",
        message: ""
    };

    incrementScore = () => {
        this.setState({ score: this.state.score + 1 })
    }

    incrementWins = () => {
        this.setState({ wins: this.state.wins + 1 })
    }

    incrementLoses = () => {
        this.setState({ loses: this.state.loses + 1 })
    }

    setScoreZero = () => {
        this.setState({ score: 0 })
    }
    
    openModal() {
        this.setState({ modalIsOpen: true });
    }

    setTitle = (title) => {
        this.setState({title: title})
    }

    setSubTitle = (subTitle) => {
        this.setState({subtitle: subTitle})
    }

    setMessage = (message) => {
        this.setState({message: message})
    }

    afterOpenModal() {
        // references are now sync'd and can be accessed.
        this.subtitle.style.color = '#f00';
     
    }

    closeModal() {
        this.setState({ modalIsOpen: false });
    }

    render() {

        return (

            <div>
                <NavBar
                    dark={true}
                    name="Mind Jar"
                    score={this.state.score}
                    wins={this.state.wins}
                    loses={this.state.loses}
                    highScore={this.state.highScore}
                />
              
               <Main
                    score={this.state.score}
                    wins={this.state.wins}
                    loses={this.state.loses}
                    highScore={this.state.highScore}
                    incrementScore={this.incrementScore}
                    incrementWins={this.incrementWins}
                    incrementLoses={this.incrementLoses}
                    setScoreZero={this.setScoreZero}
                    openModal={this.openModal}
                    setMessage={this.setMessage}
                    setSubTitle={this.setSubTitle}
                    setTitle={this.setTitle}

                   
                />
                <ReactModal
                    isOpen={this.state.modalIsOpen}
                    onAfterOpen={this.afterOpenModal}
                    onRequestClose={this.closeModal}
                    contentLabel={this.state.title}
                    customStyles={this.customStyles}

                >

                    <h2 ref={subtitle => this.subtitle = subtitle}>{this.title}</h2>
                    <button onClick={this.closeModal}>close</button>
                    <div>
                        <p>{message => this.message = message}{this.message}</p>
                    </div>

                </ReactModal>

                <nav class="navbar fixed-bottom navbar-light bg-light">
                    <a class="text" href="/">Mind Jar</a>
                </nav>
                
            </ div>

        )
    }
}

export default App;
